import { formatUnits } from '@ethersproject/units'

function App() {
	const [balance, setBalance] = useState(0)
	const [token, setToken] = useState(null)

	useEffect(() => {
		const contractAddress = '0x5B33A52Ab444203FeB4ED153f475aB60E99A6E5D'
		const userAddress = '0x25e55f2C8b889cfe4749845B056C0D878bF6b2a3' //window.ethereum.selectedAddress
